    static char[,] ConvertToCharArray(string input)
    {
        input = input.Trim().ToUpper();

        string[] rows = input.Split(new[] { "\r\n" }, StringSplitOptions.None);

        int numRows = rows.Length;
        int numCols = rows[0].Length;

        char[,] charArray = new char[numRows, numCols];

        for (int i = 0; i < numRows; i++)
        {
            for (int j = 0; j < numCols; j++)
            {
                charArray[i, j] = rows[i][j];
            }
        }

        return charArray;
    }

    // END INPUT


    // Part 1
    int CalcXmas(char[,] l)
    {
        int acc = 0;
        for (int i = 0; i < l.GetLength(0); i++)
        {
            for (int j = 0; j < l.GetLength(1); j++)
            {
                // horizontal
                if (j + 3 < l.GetLength(1)
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i, j + 1], l[i, j + 2], l[i, j + 3])) acc++;

                // horizontal back
                if (j >= 3
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i, j - 1], l[i, j - 2], l[i, j - 3])) acc++;

                // vertical 
                if (i + 3 < l.GetLength(0)
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i + 1, j], l[i + 2, j], l[i + 3, j])) acc++;

                // vertical back
                if (i >= 3
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i - 1, j], l[i - 2, j], l[i - 3, j])) acc++;

                // diagonal right down
                if (l.GetLength(0) - i > 3 && l.GetLength(1) - j > 3
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i + 1, j + 1], l[i + 2, j + 2], l[i + 3, j + 3])) acc++;

                // diagonal back
                if (i >= 3 && j >= 3
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i - 1, j - 1], l[i - 2, j - 2], l[i - 3, j - 3])) acc++;

                // diagonal left
                if (l.GetLength(0) - i > 3 && j >= 3
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i + 1, j - 1], l[i + 2, j - 2], l[i + 3, j - 3])) acc++;

                // diagonal left back
                if (i >= 3 && l.GetLength(1) - j > 3
                    && l[i, j] == 'X'
                    && IsXmas(l[i, j], l[i - 1, j + 1], l[i - 2, j + 2], l[i - 3, j + 3])) acc++;
            }
        }
        return acc;
    }

    bool IsXmas(params char[] chars) => new string(chars) == "XMAS";
    //Console.WriteLine($"Total: {CalcXmas(ConvertToCharArray(input))}");

    // Part 2
    int CalcMaxShapeX(char[,] l)
    {
        int acc = 0;
        for (int i = 1; i < l.GetLength(0) - 1; i++)
        {
            for (int j = 1; j < l.GetLength(1) - 1; j++)
            {
                if (l[i, j] == 'A')
                {
                    if (((l[i - 1, j - 1] == 'M' && l[i + 1, j + 1] == 'S') ||
                        (l[i - 1, j - 1] == 'S' && l[i + 1, j + 1] == 'M')) &&
                        ((l[i - 1, j + 1] == 'M' && l[i + 1, j - 1] == 'S') ||
                        (l[i - 1, j + 1] == 'S' && l[i + 1, j - 1] == 'M')))
                    {
                        acc++;
                    }
                }
            }
        }

        return acc;
    }
    //Console.WriteLine($"Total: {CalcMaxShapeX(ConvertToCharArray(input))}");
